import React, {useEffect, useState} from 'react';

const ListRegs = () => {
    const [prods, setProds] = useState("test");

    // The empty array is used for rendering useEffect only once
    useEffect ( () => {
        const fetchData = async () => {
            try {
                const options = {method: 'GET', headers: {'Content-Type': 'text/plain'}};
                const response = fetch('http://localhost:3001/api/v1/regs', options)
                .then(response => response.json())
                .then(response => console.log(response.rows))
                setProds(response.rows);
            } catch (error) {
                console.log(error.message)
            }
        }
        fetchData();
    }, []);

    return (
        <div className="list-group">
            <table className="table table-hover table-dark">
                <thead>
                    <tr className="bg-primary">
                        <th scope="col">Code</th>
                        <th scope="col">Name</th>
                        <th scope="col">Description</th>
                        <th scope="col">Price</th>
                        <th scope="col">Update</th>
                        <th scope="col">Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {prods.map((prod) => {
                        <tr>
                            <td>{prod.cod_prod}</td>
                            <td>{prod.prod_name}</td>
                            <td>{prod.description}</td>
                            <td>{prod.price}</td>

                        </tr>;
                    })}

                </tbody>
            </table>
        </div>
    )    
}

export default ListRegs
