import React, {useState} from 'react';

const ClientSearch= () => {
    /*
    // method onSubmitForm has a POST request
    const onSubmitForm = async (e) => {
        e.preventDefault();

        try {
            const body = {customer_id, date};

            const options = {
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(body)
            }
            const response = await fetch('http://localhost:3001/orders/api/v1/ord/', options)
            console.log(response);


            // Fast solution for table refresh
            //window.location.reload();

        } catch (error) {
            console.log(error.message);
        }

    }
    */
    return (
        <>

            <h5>Customer Search</h5>

            <button type="button" className="btn btn-warning" data-toggle="modal" data-target="#mymodal">
            Edit
            </button>

            <div className="modal" id="myModal">
            <div className="modal-dialog">
                <div className="modal-content">

                <div className="modal-header">
                    <h4 className="modal-title" style={{color: "black"}}>Edit Order</h4>
                    <button type="button" className="close" data-dismiss="modal">&times;</button>
                </div>

                <div className="modal-body">
                    <label style={{color: "black"}}>Customer Id:</label>
                    <input type="number" className="form-control" value={customer_id} onChange={(e)=>setCustomerId(parseInt(e.target.value))}/>

                    <label style={{color: "black"}}>Date:</label>
                    <input type="text" className="form-control" value={nDate(date)} onChange={(e)=>setDate(e.target.value)}/>

                    <label style={{color: "black"}}>Status:</label>
                    <input type="text" className="form-control" value={status} onChange={(e)=>setStatus(e.target.value)}/>
                </div>

                <div className="modal-footer">
                    <button type="button" className="btn btn-success" data-dismiss="modal" onClick={(e)=>setData(e)}>Confirm</button>
                    <button type="button" className="btn btn-danger" data-dismiss="modal">Close</button>
                </div>

                </div>
            </div>
            </div>


        </>
    );
};

export default ClientSearch;
